mapper.update:
update dish
name = #{name}, pic = #{pic}, detail = #{detail}, price = #{price}, category_id = #{categoryId}, create_user = #{createUser}, update_user = #{updateUser}, create_time = #{createTime}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select * from dish


        and name like concat('%', #{name}, '%')


        and category_id = #{categoryId}


        and status = #{status}

